import React from 'react';
import * as PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import GridList from '@material-ui/core/GridList';
import SearchIcon from '@material-ui/icons/Search';
import InputBase from '@material-ui/core/InputBase';
import { Slide } from 'react-slideshow-image';
import Divider from '@material-ui/core/Divider';
import { Typography } from '@material-ui/core';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';

const slideImages = [
  require("../asset/artisticSlideshow.jpg"),
  require("../asset/burgundySlideshow.jpg"),
  require("../asset/coralSlideshow.jpg"),
];

const properties = {
  duration: 5000,
  transitionDuration: 500,
  infinite: true,
  indicators: true,
  arrows: true
}

const styles = theme => ({
  root: {
    alignItems: 'center',
  },
  canvas: {
    width: "100%"
  },
  search: {
    position: 'relative',
    boxShadow: theme.shadows[1],
    transition: [["box-shadow", "0.1s"]],
    '&:hover': {
      boxShadow: theme.shadows[4],
    },
    "&:focus": {
      boxShadow: theme.shadows[3],
    },
    marginTop: "5%",
    marginLeft: "10%",
    width: '80%',
  },
  searchIcon: {
    width: "100%",
    height: '100%',
    position: 'absolute',
    display: 'flex',
    pointerEvents: "none",
    alignItems: 'center',
    justifyContent: 'center',
    paddingLeft: "95%",
    boxSizing: "border-box"
  },
  inputRoot: {
    color: 'inherit',
    width: '100%',
  },
  inputInput: {
    paddingTop: theme.spacing.unit,
    paddingRight: theme.spacing.unit,
    paddingBottom: theme.spacing.unit,
    paddingLeft: theme.spacing.unit * 2,
    transition: theme.transitions.create('width'),
    width: '80%',
  },
  slideshowContainer: {
    marginTop: "5%",
  },
  slideshow:{
    height:"400px"
  },
  divider: {
    marginTop: "5%",
    marginBottom: "5%"
  },
  hot:{
    marginLeft:"10%",
    marginRight:"10%"
  },
  card: {
    maxWidth: 320,
  },
  media: {
    height: 140,
  },
});

function home(props) {
  const { classes } = props;


  return (
    <div className={classes.root}>
      <img className={classes.canvas}
        src={require('../asset/landingPic.png')}
      />

      <div className={classes.search}>
        <div className={classes.searchIcon}>
          <SearchIcon style={{ cursor: "pointer", pointerEvents: "all" }} />
        </div>
        <InputBase
          placeholder="Searchâ€¦"
          classes={{
            root: classes.inputRoot,
            input: classes.inputInput,
          }}
        />
      </div>

      <div className={classes.slideshowContainer}>
        <Slide {...properties}>
          <div className="each-slide">
            <div className={classes.slideshow} style={{ 'backgroundImage': `url(${slideImages[0]})` }}>
              <span>Slide 1</span>
            </div>
          </div>
          <div className="each-slide">
            <div className={classes.slideshow} style={{ 'backgroundImage': `url(${slideImages[1]})`,'height':'100%' }}>
              <span>Slide 2</span>
            </div>
          </div>
          <div className="each-slide">
            <div className={classes.slideshow} style={{ 'backgroundImage': `url(${slideImages[2]})` }}>
              <span>Slide 3</span>
            </div>
          </div>
        </Slide>
      </div>

      <div className={classes.divider}>
        <Divider />
      </div>

      <div className={classes.hot}>
      <Typography variant="h2" gutterBottom stype={{marginLeft:"10%"}}>
        Hot
      </Typography>

      <Card className={classes.card}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image="/static/images/cards/contemplative-reptile.jpg"
          title="Contemplative Reptile"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            Lizard
          </Typography>
          <Typography component="p">
            Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging
            across all continents except Antarctica
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button size="small" color="primary">
          Learn More
        </Button>
      </CardActions>
    </Card>
    </div>
    </div>



  );
}

home.propTypes = {
  classes: PropTypes.object.isRequired,
};
export default withStyles(styles)(home);
