import React from 'react';
import * as PropTypes from 'prop-types';
import withStyles from '@material-ui/core/styles/withStyles';
import EventCard from './EventCard.js';
import eventInfo from '../asset/eventInfo.json';
import Grid from "@material-ui/core/Grid";

const styles = () => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        overflow: 'hidden',
        width: "80%",
        margin: "auto",
    },
});

const EventApp = ({classes}) => {
    return (
        <div className={classes.root}>
            <Grid container alignItems="center" spacing={16}>
                <Grid item xs={2}>
                    <Avatar
                        src="http://www.heyhuangshan.com/uploadfile/2018/0426/20180426094043361.jpg"
                        className={classes.bigAvatar}/>
                </Grid>
                <Grid item xs>
                    <Typography variant="h5" gutterBottom>MerchantID</Typography>
                    <Typography variant="subheading" color="textSecondary" gutterBottom>Description</Typography>
                </Grid>
            </Grid>

        <Grid className={classes.root} spacing={16} container>
            {
                eventInfo.map((event, index) => (
                    <Grid key={index} xs item>
                        <EventCard
                            abbr={event.abbr}
                            title={event.title}
                            subheader={event.subheader}
                            brief={event.brief}
                            description={event.description}
                            imageSource={event.imageSource}
                        />
                    </Grid>
                ))}
        </Grid>
        </div>
    );
};

EventApp.propTypes = {
    classes: PropTypes.object.isRequired
};

export default withStyles(styles)(EventApp);
