import React from 'react';
import * as PropTypes from 'prop-types';
import {withStyles} from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import GridList from '@material-ui/core/GridList';
import SearchIcon from '@material-ui/icons/Search';
import InputBase from '@material-ui/core/InputBase';

const styles = theme => ({
    root: {
      flexGrow: 1,
      alignItems: 'center',
      justifyContent: 'center',
    },
    grow: {
      flexGrow: 1,
    },
    grid:{
      width: "100%",
      height: "50%",
    },
    canvas: {
      position: 'absolute',
      top: 0,
      left: 0,
      bottom: 0,
      right: 0,
    },
    canvasContainer: {
      flex:1,
      alignItems: 'stretch'
    },
    candiv: {
      marginBottom: 30,
  },
  search: {
      position: 'relative',
      boxShadow: theme.shadows[1],
      transition: [["box-shadow", "0.1s"]],
      '&:hover': {
          boxShadow: theme.shadows[4],
      },
      "&:focus": {
          boxShadow: theme.shadows[3],
      },
      marginLeft: "10%",
      width: '80%',
  },
  searchIcon: {
      width: "100%",
      height: '100%',
      position: 'absolute',
      display: 'flex',
      pointerEvents: "none",
      alignItems: 'center',
      justifyContent: 'center',
      paddingLeft: "95%",
      boxSizing: "border-box"
  },
  inputRoot: {
      color: 'inherit',
      width: '100%',
  },
  inputInput: {
      paddingTop: theme.spacing.unit,
      paddingRight: theme.spacing.unit,
      paddingBottom: theme.spacing.unit,
      paddingLeft: theme.spacing.unit * 2,
      transition: theme.transitions.create('width'),
      width: '80%',
  },
});

function home(props){
  const {classes} = props;

  return (
    <div className={classes.root}>
    <img
  src={"https://image.dhgate.com/0x0s/f2-albu-g5-M01-53-D7-rBVaJFi6NDCAL_1pAAOIbm7LX1M667.jpg/luxury-european-modern-beautiful-scenery.jpg"}
  style={classes.canvas} />
  <div className={classes.candiv}>
                    <div className={classes.search}>
                        <div className={classes.searchIcon}>
                            <SearchIcon
                                color="primary"
                                onClick={this.handleSearch}
                                style={{cursor: "pointer", pointerEvents: "all"}}
                            />
                        </div>
                        <InputBase
                            placeholder="Which item do you want to check?"
                            classes={{
                                root: classes.inputRoot,
                                input: classes.inputInput,
                            }}
                            onKeyPress={this.search}
                        />
                    </div>
                </div>
    </div>
  );
}

home.propTypes = {
    classes: PropTypes.object.isRequired,
};
export default withStyles(styles)(home);
