import React from 'react';
import * as PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import GridList from '@material-ui/core/GridList';
import SearchIcon from '@material-ui/icons/Search';
import InputBase from '@material-ui/core/InputBase';

const styles = theme => ({
  root: {
    flexGrow: 1,
    alignItems: 'center',
  },
  search: {
    position: 'relative',
    boxShadow: theme.shadows[1],
    transition: [["box-shadow", "0.1s"]],
    '&:hover': {
      boxShadow: theme.shadows[4],
    },
    "&:focus": {
      boxShadow: theme.shadows[3],
    },
    marginTop: "5%",
    marginLeft: "10%",
    width: '80%',
  },
  searchIcon: {
    width: "100%",
    height: '100%',
    position: 'absolute',
    display: 'flex',
    pointerEvents: "none",
    alignItems: 'center',
    justifyContent: 'center',
    paddingLeft: "95%",
    boxSizing: "border-box"
  },
  inputRoot: {
    color: 'inherit',
    width: '100%',
  },
  inputInput: {
    paddingTop: theme.spacing.unit,
    paddingRight: theme.spacing.unit,
    paddingBottom: theme.spacing.unit,
    paddingLeft: theme.spacing.unit * 2,
    transition: theme.transitions.create('width'),
    width: '80%',
  },
});

function home(props) {
  const { classes } = props;

  return (
    <div className={classes.root}>
      <img className={classes.canvas}
        src={"https://image.dhgate.com/0x0s/f2-albu-g5-M01-53-D7-rBVaJFi6NDCAL_1pAAOIbm7LX1M667.jpg/luxury-european-modern-beautiful-scenery.jpg"}
      />

      <div className={classes.search}>
        <div className={classes.searchIcon}>
          <SearchIcon style={{ cursor: "pointer", pointerEvents: "all" }} />
        </div>
        <InputBase
          placeholder="Searchâ€¦"
          classes={{
            root: classes.inputRoot,
            input: classes.inputInput,
          }}
        />
      </div>
    </div>
  );
}

home.propTypes = {
  classes: PropTypes.object.isRequired,
};
export default withStyles(styles)(home);
