import React from 'react';
import * as PropTypes from 'prop-types';
import withStyles from '@material-ui/core/styles/withStyles';
import Grid from "@material-ui/core/Grid";
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import Spinner from './Spinner'
import Images from './Images'
import Buttons from './Buttons'
import { API_URL } from './config'

const styles = () => ({
    root: {
        display: 'flex',
        justifyContent: 'space-around',
        width: "80%",
        margin: "auto",
        marginTop: "3%"
    },
    bigAvatar: {
        width: 100,
        height: 100,
    },
    titles: {
        marginLeft: "10%",
        marginRight: "10%"
    },
    divider: {
        marginTop: "5%",
        marginBottom: "5%"
    },
});

class MerchantApp extends React.Component{

    state = {
    uploading: false,
    images: []
  }

  onChange = e => {
    const files = Array.from(e.target.files)
    this.setState({ uploading: true })

    const formData = new FormData()

    files.forEach((file, i) => {
      formData.append(i, file)
    })

    fetch(`${API_URL}/image-upload`, {
      method: 'POST',
      body: formData
    })
    .then(res => res.json())
    .then(images => {
      this.setState({ 
        uploading: false,
        images
      })
    })
  }

  removeImage = id => {
    this.setState({
      images: this.state.images.filter(image => image.public_id !== id)
    })
  }

  render(){
    const { classes } = this.props;

    const { uploading, images } = this.state

    const content = () => {
      switch(true) {
        case uploading:
          return <Spinner />
        case images.length > 0:
          return <Images images={images} removeImage={this.removeImage} />
        default:
          return <Buttons onChange={this.onChange} />
      }
    }
    return (

        <div className={classes.root}>
            <Grid container alignItems="center" spacing={16}>
                <Grid item xs={2}>
                    <Avatar
                        src="https://cdn2.iconfinder.com/data/icons/person-gender-hairstyle-clothes-variations/48/Female-Side-comb-O-neck-512.png"
                        className={classes.bigAvatar} />
                </Grid>
                <Grid item xs>
                    <Typography variant="h5" gutterBottom>MerchantID</Typography>
                    <Typography variant="subheading" color="textSecondary" gutterBottom>Description</Typography>
                </Grid>


                <Grid item xs={12}>
                    <div className={classes.divider}>
                        <Divider />
                    </div>
                </Grid>
                <Grid item xs={12}>
                    <div className={classes.titles}>
                        <Typography variant="h4">
                            New Orders
                        </Typography>
                    </div>
                </Grid>


                <Grid item xs={12}>
                    <div className={classes.divider}>
                        <Divider />
                    </div>
                </Grid>
                <Grid item xs={12}>
                    <div className={classes.titles}>
                        <Typography variant="h4">
                            All Products
                        </Typography>
                    </div>
                </Grid>


                <Grid item xs={12}>
                    <div className={classes.divider}>
                        <Divider />
                    </div>
                </Grid>
                <Grid item xs={12}>
                    <div className={classes.titles}>
                        <Typography variant="h4">
                            New Products
                        </Typography>
                    </div>
                </Grid>


                <Grid container spacing={24}>
                    <Grid item xs={3}>
                    <div className='buttons'>
          {content()}
        </div>
                    <img className={classes.canvas} src={"https://upload.wikimedia.org/wikipedia/commons/thumb/1/14/Gatto_europeo4.jpg/250px-Gatto_europeo4.jpg"}/>
                    </Grid>
                    <Grid item xs={9}>
                        <Grid container>
                            <Typography>Name</Typography>
                        </Grid>
                        <Grid container>
                            <Typography>Price</Typography>
                        </Grid>
                        <Grid container>
                            <Typography>Description</Typography>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        </div>
    );
  } 
};

MerchantApp.propTypes = {
    classes: PropTypes.object.isRequired
};

export default withStyles(styles)(MerchantApp);
