import React from 'react';
import * as PropTypes from 'prop-types';
import withStyles from '@material-ui/core/styles/withStyles';
import EventCard from './EventCard.js';
import eventInfo from '../asset/eventInfo.json';
import Grid from "@material-ui/core/Grid";
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';

const styles = () => ({
    root: {
        display: 'flex',
        justifyContent: 'space-around',
        width: "80%",
        margin: "auto",
        marginTop: "3%"
    },
    bigAvatar: {
        margin: theme.spacing.unit * 2,
        width: 85,
        height: 85,
    },
});

const EventApp = ({classes}) => {
    return (
        <div className={classes.root}>
            <Grid container alignItems="center" spacing={16}>
                <Grid item xs={3}>
                    <Avatar
                        src="https://cdn2.iconfinder.com/data/icons/person-gender-hairstyle-clothes-variations/48/Female-Side-comb-O-neck-512.png"
                        className={classes.bigAvatar}/>
                </Grid>
                <Grid item xs>
                    <Typography variant="h5" gutterBottom>MerchantID</Typography>
                    <Typography variant="subheading" color="textSecondary" gutterBottom>Description</Typography>
                </Grid>
            </Grid>
        </div>
    );
};

EventApp.propTypes = {
    classes: PropTypes.object.isRequired
};

export default withStyles(styles)(EventApp);
